import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Author from '../models/Author.js';
import mongoose from "mongoose";
// import db from "../../../server/db/conn.mjs";

const BookEntry = (props) => (
  <tr>
    <td>{props.book.title}</td>
    <td>{props.book.author}</td>
    <td>{props.book.genre}</td>
    <td>{props.book.pageNumber}</td>
    <td>{props.book.availability ? "Available" : "Not Available"}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.book._id}`}>
        Edit
      </Link>{" "}
      |
      <button
        className="btn btn-link"
        onClick={() => {
          props.deleteBook(props.book._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

export default function BookList() {
  const [books, setBooks] = useState([]);
  // const [authors, setAuthors] = useState([]);

  const [filters, setFilters] = useState({
    genre: "",
    availability: "",
    author: "",
  });

  useEffect(() => {
    async function getBooks() {
      const response = await fetch(`http://localhost:5050/books/`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      try {
        const authorsResponse = await fetch("http://localhost:5050/authors/");
        if (!authorsResponse.ok) {
          throw new Error("Failed to fetch authors");
        }
        const authorsData = await authorsResponse.json();
        console.log(authorsData);
      } catch (error) {
        console.error("Error fetching authors:", error);
      }
      // const auth = await Author.find();
      // console.log(auth);
      // const auth = db.collection('author').find();

      const data = await response.json();
      setBooks(data);
    }
    getBooks();
    return;
  }, [books.length]);

//   useEffect(() => {
//     async function fetchData() {
//       try {
//         const response = await fetch(`http://localhost:5050/books/`);
//         // const authorsResponse = await fetch("http://localhost:5050/authors/");

//         if (!response.ok) {
//           throw new Error(`Failed to fetch data: ${response.statusText}`);
//         }

//         const data = await response.json();
//         const auth = await Author.find();
// console.log(auth);
//         // const authorsData = await authorsResponse.json();
//         setBooks(data);
//         // setAuthors(authorsData);
//       } catch (error) {
//         console.error("Error fetching data:", error);
//         // Handle the error (e.g., show an error message to the user)
//       }
//     }

//     fetchData();
//   }, []);

  // Delete book
  async function deleteBook(bookId) {
    try {
      await fetch(`http://localhost:5050/books/${bookId}`, {
        method: "DELETE",
      });
      setBooks(books.filter((book) => book._id !== bookId));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  }

  async function genReport() {}

  function renderBookList() {
    return books.map((book) => {
      return (
        <BookEntry
          book={book}
          deleteBook={() => deleteBook(book._id)}
          key={book._id}
        />
      );
    });
  }

  function handleFilterChange(event) {
    const { name, value } = event.target;
    setFilters({ ...filters, [name]: value });
  }

  // This will display the table with the records of individuals.
  return (
    <div>
      <h3>Your Personal Library</h3>
      <h5 style={{ marginTop: 30 }}>Filters</h5>
      <div className="filters">
        <label style={{ marginRight: 20 }}>
          Genre:
          <input
            type="text"
            name="genre"
            value={filters.genre}
            onChange={handleFilterChange}
          />
        </label>
        <label style={{ marginRight: 20 }}>
          Availability:
          <select
            name="availability"
            value={filters.availability}
            onChange={handleFilterChange}
          >
            <option value="">All</option>
            <option value="true">Available</option>
            <option value="false">Not Available</option>
          </select>
        </label>
        {/* <label style={{ marginRight: 20 }}>
          Author:
          <select
            name="author"
            value={filters.author}
            onChange={handleFilterChange}
          >
            <option value="">All</option>
            {authors.map((author) => (
              <option key={author._id} value={author.name}>
                {author.name}
              </option>
            ))}
          </select>{" "}
        </label> */}
        <button
          onClick={genReport}
          style={{
            border: "none",
            backgroundColor: "#cccdcf",
            borderRadius: 12,
          }}
        >
          Generate Report
        </button>
      </div>
      <table className="table" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Page Number</th>
            <th>Availability</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{renderBookList()}</tbody>
      </table>
    </div>
  );
}
