import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Record = (props) => (
  <tr>
    <td>{props.book.title}</td>
    <td>{props.book.author}</td>
    <td>{props.book.genre}</td>
    <td>{props.book.publicationYear}</td>
    <td>{props.book.availability ? "Available" : "Not Available"}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.book.book_id}`}>Edit</Link> |
      <button className="btn btn-link"
        onClick={() => {
          props.deleteBook(props.book.book_id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
