import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const BookEntry = (props) => (
  <tr>
    <td>{props.book.title}</td>
    <td>{props.book.author}</td>
    <td>{props.book.genre}</td>
    <td>{props.book.publicationYear}</td>
    <td>{props.book.availability ? "Available" : "Not Available"}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.book._id}`}>Edit</Link> |
      <button className="btn btn-link"
        onClick={() => {
          props.deleteBook(props.book._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

export default function BookList() {
  const [books, setBooks] = useState([]);

  const [filters, setFilters] = useState({
    genre: "",
    availability: "",
    author: "",
    publicationYear: "",
  });

   useEffect(() => {
   async function getBooks() {
     const response = await fetch(`http://localhost:5050/books/`);
     if (!response.ok) {
       const message = `An error occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }

     const data = await response.json();
     setBooks(data);
   }

   getBooks();

   return;
 }, [books.length]);
// useEffect(() => {
//   async function getBooks() {
//     const response = await fetch(`http://localhost:5050/books/`);
//     if (!response.ok) {
//       const message = `An error occurred: ${response.statusText}`;
//       window.alert(message);
//       return;
//     }

//     const data = await response.json();
//     setBooks(data);
//     setOriginalBooks(data); 
//   }

//   getBooks();
// }, []);

  // Delete book
  async function deleteBook(bookId) {
    try {
      await fetch(`http://localhost:5050/books/${bookId}`, {
        method: "DELETE"
      });
      setBooks(books.filter((book) => book._id !== bookId));

    } catch (error) {
      console.error("Error deleting book:", error);
    }
  }

  async function genReport() {
  }

function renderBookList() {
  return books.map((book) => 
 { return (  <BookEntry
      book={book}
      deleteBook={() => deleteBook(book._id)}
      key={book._id}
    />)}
  );
}

function handleFilterChange(event) {
  const { name, value } = event.target;
  setFilters({ ...filters, [name]: value });
}

 // This will display the table with the records of individuals.
 return (
  <div>
  <h3>Your Personal Library</h3>
  <h5>Filters</h5>
  <div className="filters">
        <label>
          Genre:
          <input type="text" name="genre" value={filters.genre} onChange={handleFilterChange}/>
        </label>
        <label>
          Availability:
          <select name="availability" value={filters.availability} onChange={handleFilterChange} >
            <option value="">All</option>
            <option value="true">Available</option>
            <option value="false">Not Available</option>
          </select>
        </label>
        <label>
          Author:
          <input type="text" name="author" value={filters.author} onChange={handleFilterChange} />
        </label>
        <label>
          Publication Year:
          <input
            type="number"
            name="publicationYear"
            value={filters.publicationYear}
            onChange={handleFilterChange}          />
        </label>
        <button onClick={genReport}>Generate Report</button>
      </div>
  <table className="table" style={{ marginTop: 20 }}>
    <thead>
      <tr>
        <th>Title</th>
        <th>Author</th>
        <th>Genre</th>
        <th>Publication Year</th>
        <th>Availability</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>{renderBookList()}</tbody>
  </table>
</div>
 );
}
