import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const BookEntry = (props) => (
  <tr>
    <td>{props.book.title}</td>
    <td>{props.book.author}</td>
    <td>{props.book.genre}</td>
    <td>{props.book.pageNumber}</td>
    <td>{props.book.availability ? "Available" : "Not Available"}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.book._id}`}>
        Edit
      </Link>{" "}
      |
      <button
        className="btn btn-link"
        onClick={() => {
          props.deleteBook(props.book._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

export default function BookList() {
  const [books, setBooks] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [genres, setGenres] = useState([]);

  const [filters, setFilters] = useState({
    genre: "",
    availability: "",
    author: "",
  });

  useEffect(() => {
    async function getBooks() {
      const response = await fetch(`http://localhost:5050/books/`);
      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const data = await response.json();
      const uniqueAuthors = [...new Set(data.map(book => book.author))];
      setAuthors(uniqueAuthors);
      const uniqueGenres = [...new Set(data.map(book => book.genre))];
      setGenres(uniqueGenres);
      setBooks(data);
    }
    getBooks();
    return;
  }, [books.length]);

  // Delete book
  async function deleteBook(bookId) {
    try {
      await fetch(`http://localhost:5050/books/${bookId}`, {
        method: "DELETE",
      });
      setBooks(books.filter((book) => book._id !== bookId));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  }

  async function genReport() {
    let filteredBooks = books;

    // Apply filters
    if (filters.genre) {
      filteredBooks = filteredBooks.filter((book) => book.genre === filters.genre);
    }
    if (filters.availability !== "") {
      const availability = filters.availability === "true";
      filteredBooks = filteredBooks.filter((book) => book.availability === availability);
    }
    if (filters.author) {
      filteredBooks = filteredBooks.filter((book) => book.author === filters.author);
    }

    // Calculate statistics
    const totalBooks = filteredBooks.length;
    let totalPageNumber = 0;
    // const totalPageNumber = filteredBooks.reduce((acc, book) => acc + book.pageNumber, 0);
    for (let i = 0; i < filteredBooks.length; i++) {
      totalPageNumber += filteredBooks[i].pageNumber;
    }
    const avgPageNumber = totalPageNumber / filteredBooks.length || 0;

    // const avgPageNumber = filteredBooks.reduce((sum, book) => sum + book.pageNumber, 0) / totalBooks;

    // Find most borrowed books
    // const mostBorrowedBooks = filteredBooks
    //   .sort((a, b) => b.borrowCount - a.borrowCount) // Sort by borrow count in descending order
    //   .slice(0, 5); // Take top 5 most borrowed books

    // Display the report
    console.log("Filtered:", filteredBooks);
    console.log("Total Books:", totalBooks);
    console.log("Average Page Number:", avgPageNumber);
    // console.log("Most Borrowed Books:", mostBorrowedBooks);

    // You can render this information in a separate component or modal
    // Example:
    // setShowReportModal(true);
    // And then in your JSX, conditionally render the modal based on the state setShowReportModal

  }

  function renderBookList() {
    return books.map((book) => {
      return (
        <BookEntry
          book={book}
          deleteBook={() => deleteBook(book._id)}
          key={book._id}
        />
      );
    });
  }

  function handleFilterChange(event) {
    const { name, value } = event.target;
    setFilters({ ...filters, [name]: value });
  }

  // This will display the table with the records of individuals.
  return (
    <div>
      <h3>Your Personal Library</h3>
      <h5 style={{ marginTop: 30 }}>Filters</h5>
      <div className="filters">
        <label style={{ marginRight: 20 }}>
          Genre:
          <select
            name="genre"
            value={filters.genre}
            onChange={handleFilterChange}
          >
            <option value="">All</option>
            {genres.map((genre) => (
              <option key={genre} value={genre}>
                {genre}
              </option>
            ))}
          </select>
        </label>
        <label style={{ marginRight: 20 }}>
          Availability:
          <select
            name="availability"
            value={filters.availability}
            onChange={handleFilterChange}
          >
            <option value="">All</option>
            <option value="true">Available</option>
            <option value="false">Not Available</option>
          </select>
        </label>
        <label style={{ marginRight: 20 }}>
          Author:
          <select
            name="author"
            value={filters.author}
            onChange={handleFilterChange}
          >
            <option value="">All</option>
            {authors.map((author) => (
              <option key={author} value={author}>
                {author}
              </option>
            ))}
          </select>
        </label>
        <button
          onClick={genReport}
          style={{
            border: "none",
            backgroundColor: "#cccdcf",
            borderRadius: 12,
          }}
          onMouseEnter={(e) => { e.target.style.backgroundColor = "#8c8c8c"; }}
          onMouseLeave={(e) => { e.target.style.backgroundColor = "#cccdcf"; }}
        >
          Generate Report
        </button>
      </div>
      <table className="table" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Page Number</th>
            <th>Availability</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{renderBookList()}</tbody>
      </table>
    </div>
  );
}
